import React, { useState, useEffect } from "react";
import "./item.css";

const ItemList = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/api/v1/products")
      .then((response) => response.json())
      .then((data) => {
        setItems(data);
      })
      .catch((error) => console.error("Error fetching items:", error));
  }, []);

  const handleAddToCart = async (productId) => {
    try {
      const response = await fetch("http://localhost:3000/api/v1/cart", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          token: localStorage.token,
        },
      });

      if (response.ok) {
        const cartData = await response.json();
        const cartId = cartData.cart_id;

        // Now you have the cart ID, you can add the product to the cart
        const addToCartResponse = await fetch(
          `http://localhost:3000/api/v1/cartitem/${cartId}`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              token: localStorage.token,
            },
            body: JSON.stringify({ productid: productId }),
          }
        );

        if (addToCartResponse.ok) {
          console.log("Product added to cart successfully!");
        } else {
          console.error("Failed to add product to cart.");
        }
      } else {
        console.error("Failed to create or retrieve cart.");
      }
    } catch (error) {
      console.error("Error adding product to cart:", error);
    }
  };

  return (
    <div className="item-list-container">
      <h2 className="products-header">Products</h2>
      <ul>
        {items.map((item) => (
          <li key={item.productid} className="item">
            <div>
              <h3 className="item-name">
                {item.name} | {item.price}
              </h3>
              <p className="item-description">{item.description}</p>
              <button onClick={() => handleAddToCart(item.productid)}>
                Add to Cart
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ItemList;
