import React, { useState, useEffect } from "react";
import "./cart.css";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Fetch cart items from the server and update the state
    // Make a request to retrieve cart items based on the user's cart ID
    // Update the cartItems state with the response
  }, []);

  // Calculate the total price of items in the cart
  const calculateTotal = () => {
    return cartItems.reduce(
      (total, cartItem) => total + cartItem.quantity * cartItem.price,
      0
    );
  };

  return (
    <div className="cart-container">
      <h2 className="cart-header">Shopping Cart</h2>
      <ul>
        {cartItems.map((cartItem) => (
          <li key={cartItem.cartitem_id}>
            {cartItem.quantity} x {cartItem.product_name} - ${cartItem.price}
          </li>
        ))}
      </ul>
      <p>Total: ${calculateTotal()}</p>
    </div>
  );
};

export default Cart;
