import React, { useState, useEffect } from "react";
import "./cart.css";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Fetch cart items from the server and update the state
    // Make a request to retrieve cart items based on the user's cart ID
    // Update the cartItems state with the response
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      const cartResponse = await fetch("http://localhost:3000/api/v1/cart", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          token: localStorage.token,
        },
      });

      if (cartResponse.ok) {
        const cartData = await cartResponse.json();
        const cartId = cartData.cart_id;

        const cartItemsResponse = await fetch(
          `http://localhost:3000/api/v1/cartitem/${cartId}`
        );

        if (cartItemsResponse.ok) {
          const cartItemsData = await cartItemsResponse.json();
          setCartItems(cartItemsData);
        } else {
          console.error("Failed to fetch cart items.");
        }
      } else {
        console.error("Failed to create or retrieve cart.");
      }
    } catch (error) {
      console.error("Error fetching cart:", error);
    }
  };

  // Calculate the total price of items in the cart
  const calculateTotal = () => {
    return cartItems.reduce(
      (total, cartItem) => total + cartItem.quantity * cartItem.price,
      0
    );
  };

  return (
    <div className="cart-wrapper">
      <div className="cart-container">
        <h2 className="cart-header">Shopping Cart</h2>
        <ul className="cart-list">
          {cartItems.map((cartItem) => (
            <li className="item-name" key={cartItem.cartitem_id}>
              {cartItem.quantity} x {cartItem.product_name} - ${cartItem.price}
            </li>
          ))}
        </ul>
        <p className="total">Total: ${calculateTotal()}</p>
      </div>
    </div>
  );
};

export default Cart;
