import React from "react";
import { useHistory } from "react-router-dom";
import logoutImage from "./pics/logout.png";
import checkoutImage from "./pics/checklist.png";
import "./Header.css";

const Header = ({ name, logout }) => {
  const history = useHistory();

  const googleLogoutfunc = async () => {
    // Log out logic for Google API should be placed here.
    // For demonstration purposes, only app logout is shown.
    googleLogout();
    localStorage.removeItem("token"); // Remove the token from local storage
    console.log("Logged out from the app.");
    // If using Google's API to log out, you would call the appropriate method here.
    // Example with gapi (Make sure gapi is loaded and initialized):
    // const auth2 = window.gapi.auth2.getAuthInstance();
    // if (auth2 != null) {
    //   auth2.signOut().then(() => console.log('Google logout successful'));
    // }
  };

  const handleCheckout = () => {
    history.push("/order");
  };

  return (
    <header className="header-container">
      <button className="logout-button" onClick={logout}>
        <img src={logoutImage} alt="Logout" className="logout-image" />
      </button>
      <h3 className="welcome-message">Welcome, {name}</h3>
      <h1 className="header-title">X-Fitness</h1>
      <button className="checkout-button" onClick={handleCheckout}>
        <img src={checkoutImage} alt="Checkout" className="checkout-image" />
      </button>
    </header>
  );
};

export default Header;
