import React, { useState, useEffect } from "react";
import "./cart.css";

export const fetchCartItems = async (setCartItems, setFetchTrigger) => {
  console.log("Fetching cart items...");
  console.log(localStorage.token);
  try {
    const userResponse = await fetch(
      "http://localhost:3000/api/v1/auth/user-data",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          token: localStorage.token,
        },
      }
    );

    console.log("User data received...");

    if (!userResponse.ok) {
      console.error("Failed to retrieve user information.");
      return;
    }

    const userData = await userResponse.json();
    const userId = userData.id;

    const cartResponse = await fetch(
      `http://localhost:3000/api/v1/cart/user/${userId}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.token}`,
        },
      }
    );

    console.log("Cart data received...");

    if (cartResponse.ok) {
      const cartData = await cartResponse.json();

      if (cartData.length > 0) {
        const cartId = cartData[0].cart_id;

        const cartItemsResponse = await fetch(
          `http://localhost:3000/api/v1/cartitem/${cartId}`
        );

        if (cartItemsResponse.ok) {
          const cartItemsData = await cartItemsResponse.json();

          const quantityDict = {};
          const updatedCartItems = [];

          for (const cartItem of cartItemsData) {
            // Check if productid has been processed
            if (!quantityDict[cartItem.productid]) {
              const productResponse = await fetch(
                `http://localhost:3000/api/v1/products/${cartItem.productid}`
              );

              if (productResponse.ok) {
                const productData = await productResponse.json();

                // Count occurrences of the same productid in cartItemsData
                const productidCount = cartItemsData.filter(
                  (item) => item.productid === cartItem.productid
                ).length;

                updatedCartItems.push({
                  ...cartItem,
                  name: productData[0].name,
                  price: productData[0].price * productidCount,
                  quantity: productidCount,
                });
                console.log("On-screen cart items updated...");
                // Mark productid as processed
                quantityDict[cartItem.productid] = true;
              } else {
                console.error(
                  `Failed to fetch product details for product ID ${cartItem.productid}.`
                );
              }
            }
          }

          // Compare with the existing cart items
          if (!arraysEqual(updatedCartItems, setCartItems)) {
            setCartItems(updatedCartItems);

            // Call refreshCart to update the cart on the screen
            await refreshCart({ setCartItems: setCartItems });

            // Trigger the first useEffect
            setFetchTrigger((prev) => !prev);

            // Resolve the Promise with the updated cart items
            return Promise.resolve(updatedCartItems);
          } else {
            // Cart items are already up to date, resolve with the current items
            return Promise.resolve(setCartItems);
          }
        } else {
          console.error(
            "Failed to fetch cart items.",
            cartItemsResponse.statusText
          );

          // Reject the Promise if there's an error
          return Promise.reject("Failed to fetch cart items");
        }
      } else {
        // If the cart does not exist, create a new cart
        const createCartResponse = await fetch(
          `http://localhost:3000/api/v1/cart/${userId}`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${localStorage.token}`,
            },
            body: JSON.stringify({
              user_id: userId, // Include the user ID obtained from the token
            }),
          }
        );

        if (createCartResponse.ok) {
          const newCartData = await createCartResponse.json();
          const newCartId = newCartData.cart_id;

          const newCartItemsResponse = await fetch(
            `http://localhost:3000/api/v1/cartitem/${newCartId}`
          );

          if (newCartItemsResponse.ok) {
            const newCartItemsData = await newCartItemsResponse.json();

            // Check if there are any cart items
            if (newCartItemsData.length > 0) {
              setCartItems(newCartItemsData);
            } else {
              console.log("No cart items found for the new cart.");
              // Handle the case when there are no cart items
            }

            // Resolve the Promise with the updated cart items
            return Promise.resolve(newCartItemsData);
          } else {
            console.error(
              "Failed to fetch cart items for the new cart.",
              newCartItemsResponse.statusText
            );

            // Reject the Promise if there's an error
            return Promise.reject("Failed to fetch cart items");
          }
        } else {
          console.error("Failed to create or retrieve cart for the user.");

          // Reject the Promise if there's an error
          return Promise.reject("Failed to create or retrieve cart");
        }
      }
    } else {
      console.error(
        "Failed to fetch user cart information.",
        cartResponse.statusText
      );

      // Reject the Promise if there's an error
      return Promise.reject("Failed to fetch user cart information");
    }
  } catch (error) {
    console.error("Error fetching cart:", error);

    // Reject the Promise if there's an error
    return Promise.reject("Error fetching cart");
  }
};

export const refreshCart = async ({ setCartItems }) => {
  console.log("Refreshing cart...");
  try {
    // Fetch updated cart items and pass a callback to update the state
    await fetchCartItems(setCartItems);
  } catch (error) {
    console.error("Error refreshing cart:", error);
  }
};

export const Cart = ({ setCartItems }) => {
  const [cartItems, setCartItemsLocal] = useState([]);
  const [fetchTrigger, setFetchTrigger] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch updated cart items and pass a callback to update the state
        const updatedItems = await fetchCartItems(
          setCartItemsLocal,
          setFetchTrigger
        );

        // Check if cartItems is an array before attempting to update the state
        if (Array.isArray(updatedItems)) {
          // Example: Update the component state to trigger a re-render
          setCartItems([...updatedItems]); // Create a new array with the same elements
        }
      } catch (error) {
        console.error("Error fetching cart:", error);
      }
    };

    fetchData();
  }, [fetchTrigger]);

  useEffect(() => {
    console.log("Cart items updated:", cartItems);

    if (Array.isArray(cartItems)) {
      setCartItems([...cartItems]);
    }
  }, [cartItems, setCartItems]);

  const calculateTotal = () => {
    if (!cartItems || !Array.isArray(cartItems)) {
      return 0;
    }

    // Create an object to store the quantity of each product
    const productQuantities = {};

    // Count the occurrences of each productid in the cartItems array
    cartItems.forEach((cartItem) => {
      const { productid } = cartItem;
      productQuantities[productid] = (productQuantities[productid] || 0) + 1;
    });

    // Calculate the total based on the quantity and price of each product
    const total = Object.keys(productQuantities).reduce((acc, productid) => {
      const quantity = productQuantities[productid];
      const product = cartItems.find(
        (item) => item.productid === parseInt(productid)
      );

      if (product) {
        return acc + quantity * product.price;
      }
      return acc;
    }, 0);

    return total;
  };

  return (
    <div className="cart-wrapper">
      <div className="cart-container">
        <h2 className="cart-header">Shopping Cart</h2>
        <ul className="cart-list">
          {cartItems &&
            Array.isArray(cartItems) &&
            cartItems.map((cartItem) => (
              <li className="item-name" key={cartItem.cartitem_id}>
                {cartItem.quantity} x {cartItem.name} - ${cartItem.price}
              </li>
            ))}
        </ul>
        <p className="total">Total: ${calculateTotal()}</p>
      </div>
    </div>
  );
};

const arraysEqual = (arr1, arr2) => {
  if (arr1.length !== arr2.length) return false;
  for (let i = 0; i < arr1.length; i++) {
    if (arr1[i] !== arr2[i]) return false;
  }
  return true;
};

export default Cart;
