import React, { useState } from "react";
import { Link } from "react-router-dom";
import { GoogleLogin, googleLogout } from "@react-oauth/google";
import "./login.css"; // Import your login styles

const clientId =
  "65793818011-ggo7gienivg0jn9j0kfno8t37c7infam.apps.googleusercontent.com";

export const LoginGoogle = ({ setAuth }) => {
  const onSuccess = async (googleData) => {
    try {
      console.log("Google data received on client:", googleData);
      const { credential } = googleData;

      const response = await fetch(
        "http://localhost:3000/api/v1/auth/oauth/google",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ access_token: credential }), // Send the ID token as access_token
        }
      );

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const parseRes = await response.json();

      localStorage.setItem("token", parseRes.token);
      setAuth(true);
    } catch (err) {
      console.error("Error during login: ", err);
    }
  };

  const onFailure = (res) => {
    console.log("LOGIN FAILED! Response: ", res);
  };

  const handleLogout = async () => {
    try {
      // Call the googleLogout function from the @react-oauth/google package
      await googleLogout();

      // Remove the JWT token from localStorage
      localStorage.removeItem("token");

      // Redirect the user to the login screen
      window.location = "/login";
    } catch (error) {
      console.error("Logout failed", error);
      // Handle any errors that might occur during the logout process
    }
  };

  return (
    <div id="signInButton">
      <GoogleLogin
        clientId={clientId}
        buttonText="Login"
        onSuccess={onSuccess}
        onFailure={onFailure}
        cookiePolicy={"single_host_origin"}
        isSignedIn={true}
      />
    </div>
  );
};

export const Login = ({ setAuth }) => {
  const [inputs, setInputs] = useState({
    email: "",
    password: "",
  });

  const { email, password } = inputs;

  const onChange = (e) => {
    setInputs({ ...inputs, [e.target.name]: e.target.value });
  };

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { email, password };

      const response = await fetch("http://localhost:3000/api/v1/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      const parseRes = await response.json();

      localStorage.setItem("token", parseRes.token);

      setAuth(true);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1>User Login</h1>
        <form onSubmit={onSubmitForm} className="login-form">
          <div className="input-group">
            <input
              type="email"
              name="email"
              placeholder="Username"
              value={email}
              onChange={onChange}
            />
          </div>
          <div className="input-group">
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={password}
              onChange={onChange}
            />
          </div>
          <button type="submit">Login</button>
        </form>
        <Link to="/register" className="register-link">
          Register
        </Link>
        <div className="google-auth">
          <LoginGoogle setAuth={setAuth} />
        </div>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
  );
};

export default Login;
