import React, { useEffect } from "react";
import { useHistory } from "react-router-dom";
import logoutImage from "./pics/logout.png";
import checkoutImage from "./pics/checklist.png";
import "./Header.css";

const Header = ({ name, logout }) => {
  const history = useHistory();

  useEffect(() => {
    window.gapi.load("auth2", () => {
      window.gapi.auth2.init({
        client_id:
          "65793818011-ggo7gienivg0jn9j0kfno8t37c7infam.apps.googleusercontent.com",
      });
    });
  }, []);

  const handleCheckout = () => {
    history.push("/order");
  };

  const logoutGoogle = async () => {
    try {
      const auth2 = window.gapi.auth2.getAuthInstance();
      if (auth2 != null) {
        await auth2.signOut();
        auth2.disconnect().then(() => {
          logout(); // Your local logout logic.
        });
      } else {
        console.log("Auth2 not initialized");
        // Handle the case where auth2 is not initialized.
      }
    } catch (error) {
      console.error("Error signing out of Google:", error);
    }
  };

  return (
    <header className="header-container">
      <button className="logout-button" onClick={logoutGoogle}>
        <img src={logoutImage} alt="Logout" className="logout-image" />
      </button>
      <h3 className="welcome-message">Welcome, {name}</h3>
      <h1 className="header-title">X-Fitness</h1>
      <button className="checkout-button" onClick={handleCheckout}>
        <img src={checkoutImage} alt="Checkout" className="checkout-image" />
      </button>
    </header>
  );
};

export default Header;
