import React, { useState } from "react";
import { OAuthProvider, useOAuth } from "react-oauth";
import { GoogleLogin } from "react-google-login";
import "./login.css"; // Import your login styles

const clientId =
  "65793818011-ggo7gienivg0jn9j0kfno8t37c7infam.apps.googleusercontent.com";

function LoginGoogle() {
  return (
    <div id="signInButton">
      <GoogleLogin
        clientId={clientId}
        buttonText="Login"
        onSuccess={onSuccess}
        onFailure={onFailure}
        cookiePolicy={"single_host_origin"}
        isSignedIn={true}
      />
    </div>
  );
}

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const oauth = useOAuth({
    provider: "google",
    clientId: "YOUR_GOOGLE_CLIENT_ID", // Replace with your Google OAuth App client ID
    redirectUri: "http://localhost:3000/callback", // Replace with your redirect URI
  });

  const handleLogin = (e) => {
    e.preventDefault();
    // Your existing login logic here (optional if you want to handle traditional login)
    // For OAuth, you can use the oauth.open() function to initiate the Google OAuth flow
    oauth.open();
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>

      {/* OAuthProvider component to handle OAuth flow */}
      <OAuthProvider {...oauth}>
        {/* YourCallbackComponent goes here */}
      </OAuthProvider>
    </div>
  );
};

export default Login;
