import React, { useState } from "react";
import { Link } from "react-router-dom";
import { GoogleLogin } from "@react-oauth/google";
import "./login.css"; // Import your login styles

const clientId =
  "65793818011-ggo7gienivg0jn9j0kfno8t37c7infam.apps.googleusercontent.com";

export const LoginGoogle = () => {
  const onSuccess = (res) => {
    console.log("LOGIN SUCCESS! Response: ", res);

    // Check the structure of the response object and log the user information
    const userProfile = res?.profileObj || res?.profileObj;

    if (userProfile) {
      console.log("User Profile: ", userProfile);
    } else {
      console.log("User Profile not found in the response.");
    }
  };

  const onFailure = (res) => {
    console.log("LOGIN FAILED! Response: ", res);
  };

  return (
    <div id="signInButton">
      <GoogleLogin
        clientId={clientId}
        buttonText="Login"
        onSuccess={onSuccess}
        onFailure={onFailure}
        cookiePolicy={"single_host_origin"}
        isSignedIn={true}
      />
    </div>
  );
};

export const Login = ({ setAuth }) => {
  const [inputs, setInputs] = useState({
    email: "",
    password: "",
  });

  const { email, password } = inputs;

  const onChange = (e) => {
    setInputs({ ...inputs, [e.target.name]: e.target.value });
  };

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { email, password };

      const response = await fetch("http://localhost:3000/api/v1/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      const parseRes = await response.json();

      localStorage.setItem("token", parseRes.token);

      setAuth(true);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <div className="login-container">
      <h2>User Login</h2>
      <form onSubmit={onSubmitForm} className="login-form">
        <input
          type="email"
          name="email"
          placeholder="Username"
          value={email}
          onChange={onChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={password}
          onChange={onChange}
        />
        <button type="submit">Login</button>
      </form>
      <Link to="/register" className="register-link">
        Register
      </Link>
      <div className="google-auth">
        <LoginGoogle />
      </div>
    </div>
  );
};

export default Login;
