import React from "react";
import { useHistory } from "react-router-dom";
import { googleLogout } from "@react-oauth/google"; // This is the logout function from the Google OAuth library
import logoutImage from "./pics/logout.png";
import checkoutImage from "./pics/checklist.png";
import "./Header.css";

const Header = ({ name, setAuth }) => {
  const history = useHistory();

  // This function will handle the logout process
  const handleLogout = async () => {
    try {
      await googleLogout();
      localStorage.removeItem("token");
      setAuth(false);
      history.push("/login"); // Use history to navigate
    } catch (error) {
      console.error("Logout failed", error);
    }
  };

  const handleCheckout = () => {
    history.push("/order");
  };

  return (
    <header className="header-container">
      <button className="logout-button" onClick={handleLogout}>
        <img src={logoutImage} alt="Logout" className="logout-image" />
      </button>
      <h3 className="welcome-message">Welcome, {name}</h3>
      <h1 className="header-title">X-Fitness</h1>
      <button className="checkout-button" onClick={handleCheckout}>
        <img src={checkoutImage} alt="Checkout" className="checkout-image" />
      </button>
    </header>
  );
};

export default Header;
