import React, { useState } from "react";
import { GoogleLogin } from "@react-oauth/google";
import "./login.css"; // Import your login styles

const clientId =
  "65793818011-ggo7gienivg0jn9j0kfno8t37c7infam.apps.googleusercontent.com";

export const LoginGoogle = () => {
  const onSuccess = (res) => {
    console.log("LOGIN SUCCESS! Response: ", res);

    // Check the structure of the response object and log the user information
    const userProfile = res?.profileObj || res?.profileObj;

    if (userProfile) {
      console.log("User Profile: ", userProfile);
    } else {
      console.log("User Profile not found in the response.");
    }
  };

  const onFailure = (res) => {
    console.log("LOGIN FAILED! Response: ", res);
  };

  return (
    <div id="signInButton">
      <GoogleLogin
        clientId={clientId}
        buttonText="Login"
        onSuccess={onSuccess}
        onFailure={onFailure}
        cookiePolicy={"single_host_origin"}
        isSignedIn={true}
      />
    </div>
  );
};

export const Login = ({ setAuth }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    // Your existing login logic here (optional if you want to handle traditional login)
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <label>
          Email:
          <input
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <br />
        <button type="submit" onClick={() => setAuth(true)}>
          Login
        </button>
      </form>

      {/* Render the Google login component */}
      <LoginGoogle />
    </div>
  );
};
