// cart.js

import React, { useState, useEffect } from "react";
import "./cart.css";

export const fetchCartItems = async () => {
  console.log("Fetching cart items...");
  console.log(localStorage.token);
  try {
    const userResponse = await fetch(
      "http://localhost:3000/api/v1/auth/user-data",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          token: localStorage.token,
        },
      }
    );

    console.log("User data received...");

    if (!userResponse.ok) {
      console.error("Failed to retrieve user information.");
      return [];
    }

    const userData = await userResponse.json();
    const userId = userData.id;

    const cartResponse = await fetch(
      `http://localhost:3000/api/v1/cart/user/${userId}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.token}`,
        },
      }
    );

    console.log("Cart data received...");

    if (cartResponse.ok) {
      const cartData = await cartResponse.json();

      if (cartData.length > 0) {
        const cartId = cartData[0].cart_id;

        const cartItemsResponse = await fetch(
          `http://localhost:3000/api/v1/cartitem/${cartId}`
        );

        if (cartItemsResponse.ok) {
          const cartItemsData = await cartItemsResponse.json();

          const quantityDict = {};
          const updatedCartItems = [];

          for (const cartItem of cartItemsData) {
            // Check if productid has been processed
            if (!quantityDict[cartItem.productid]) {
              const productResponse = await fetch(
                `http://localhost:3000/api/v1/products/${cartItem.productid}`
              );

              if (productResponse.ok) {
                const productData = await productResponse.json();

                // Count occurrences of the same productid in cartItemsData
                const productidCount = cartItemsData.filter(
                  (item) => item.productid === cartItem.productid
                ).length;

                updatedCartItems.push({
                  ...cartItem,
                  name: productData[0].name,
                  price: productData[0].price * productidCount,
                  quantity: productidCount,
                });
                console.log("On-screen cart items updated...");
                // Mark productid as processed
                quantityDict[cartItem.productid] = true;
              } else {
                console.error(
                  `Failed to fetch product details for product ID ${cartItem.productid}.`
                );
              }
            }
          }

          return updatedCartItems;
        } else {
          console.error(
            "Failed to fetch cart items.",
            cartItemsResponse.statusText
          );

          return [];
        }
      } else {
        // If the cart does not exist, return an empty array
        return [];
      }
    } else {
      console.error(
        "Failed to fetch user cart information.",
        cartResponse.statusText
      );

      return [];
    }
  } catch (error) {
    console.error("Error fetching cart:", error);

    return [];
  }
};

export const Cart = ({ setCartItems }) => {
  const [cartItems, setCartItemsLocal] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const updatedItems = await fetchCartItems();

        setCartItems([...updatedItems]);
      } catch (error) {
        console.error("Error fetching cart:", error);
      }
    };

    fetchData();
  }, [setCartItemsLocal]);

  useEffect(() => {
    console.log("Cart items updated:", cartItems);

    if (Array.isArray(cartItems)) {
      setCartItems([...cartItems]);
    }
  }, [cartItems, setCartItems]);

  const calculateTotal = () => {
    if (!cartItems || !Array.isArray(cartItems)) {
      return 0;
    }

    const productQuantities = {};

    cartItems.forEach((cartItem) => {
      const { productid } = cartItem;
      productQuantities[productid] = (productQuantities[productid] || 0) + 1;
    });

    const total = Object.keys(productQuantities).reduce((acc, productid) => {
      const quantity = productQuantities[productid];
      const product = cartItems.find(
        (item) => item.productid === parseInt(productid)
      );

      if (product) {
        return acc + quantity * product.price;
      }
      return acc;
    }, 0);

    return total;
  };

  return (
    <div className="cart-wrapper">
      <div className="cart-container">
        <h2 className="cart-header">Shopping Cart</h2>
        <ul className="cart-list">
          {cartItems &&
            Array.isArray(cartItems) &&
            cartItems.map((cartItem) => (
              <li className="item-name" key={cartItem.cartitem_id}>
                {cartItem.quantity} x {cartItem.name} - ${cartItem.price}
              </li>
            ))}
        </ul>
        <p className="total">Total: ${calculateTotal()}</p>
      </div>
    </div>
  );
};

export default Cart;
