import React, { useState, useEffect } from "react";
import "./cart.css";

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [cartId, setCartId] = useState(null); // State to store cart ID

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await fetch("http://localhost:3000/api/v1/cart", {
          headers: {
            "Content-Type": "application/json",
            token: localStorage.token, // Assuming you have a token for authentication
          },
        });

        if (response.ok) {
          const cartData = await response.json();
          setCartId(cartData.cart_id);
          // Now you have the cart ID, you can fetch cart items
          // Update the state with cart items
        } else {
          console.error("Failed to fetch cart information.");
        }
      } catch (error) {
        console.error("Error fetching cart information:", error);
      }
    };

    fetchCart();
  }, []); // Run this effect only once when the component mounts

  // Calculate the total price of items in the cart
  const calculateTotal = () => {
    return cartItems.reduce(
      (total, cartItem) => total + cartItem.quantity * cartItem.price,
      0
    );
  };

  return (
    <div className="cart-wrapper">
      <div className="cart-container">
        <h2 className="cart-header">Shopping Cart</h2>
        <ul className="cart-list">
          {cartItems.map((cartItem) => (
            <li className="item-name" key={cartItem.cartitem_id}>
              {cartItem.quantity} x {cartItem.product_name} - ${cartItem.price}
            </li>
          ))}
        </ul>
        <p className="total">Total: ${calculateTotal()}</p>
      </div>
    </div>
  );
};

export default Cart;
