import React, { useState } from "react";
import { Link } from "react-router-dom";
import { GoogleLogin, googleLogout } from "@react-oauth/google";
import "./login.css";

const clientId =
  "65793818011-ggo7gienivg0jn9j0kfno8t37c7infam.apps.googleusercontent.com";

// The custom googleLogout function
const googleLogoutfunc = async () => {
  // Log out logic for Google API should be placed here.
  // For demonstration purposes, only app logout is shown.
  googleLogout();
  localStorage.removeItem("token"); // Remove the token from local storage
  console.log("Logged out from the app.");
  // If using Google's API to log out, you would call the appropriate method here.
  // Example with gapi (Make sure gapi is loaded and initialized):
  // const auth2 = window.gapi.auth2.getAuthInstance();
  // if (auth2 != null) {
  //   auth2.signOut().then(() => console.log('Google logout successful'));
  // }
};

export const LoginGoogle = ({ setAuth }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const onSuccess = async (googleData) => {
    try {
      console.log("Google data received on client:", googleData);
      const { credential } = googleData;

      const response = await fetch(
        "http://localhost:3000/api/v1/auth/oauth/google",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ access_token: credential }),
        }
      );

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const parseRes = await response.json();
      localStorage.setItem("token", parseRes.token);
      setAuth(true);
      setIsAuthenticated(true);
    } catch (err) {
      console.error("Error during login: ", err);
    }
  };

  const onFailure = (res) => {
    console.log("LOGIN FAILED! Response: ", res);
  };

  const handleLogout = () => {
    googleLogoutfunc();
    console.log("Logout button clicked"); // Placeholder for actual logout functionality
  };

  return (
    <div className="google-auth">
      <GoogleLogin
        clientId={clientId}
        buttonText="Login"
        onSuccess={onSuccess}
        onFailure={onFailure}
        cookiePolicy={"single_host_origin"}
        isSignedIn={true}
      />
      <button onClick={handleLogout}>Logout</button>{" "}
      {/* Logout button always visible */}
    </div>
  );
};

export const Login = ({ setAuth }) => {
  const [inputs, setInputs] = useState({
    email: "",
    password: "",
  });

  const { email, password } = inputs;

  const onChange = (e) => {
    setInputs({ ...inputs, [e.target.name]: e.target.value });
  };

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { email, password };

      const response = await fetch("http://localhost:3000/api/v1/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      const parseRes = await response.json();

      localStorage.setItem("token", parseRes.token);
      setAuth(true);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1>User Login</h1>
        <form onSubmit={onSubmitForm} className="login-form">
          <div className="input-group">
            <input
              type="email"
              name="email"
              placeholder="Username"
              value={email}
              onChange={onChange}
              required
            />
          </div>
          <div className="input-group">
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={password}
              onChange={onChange}
              required
            />
          </div>
          <button type="submit">Login</button>
        </form>
        <Link to="/register" className="register-link">
          Register
        </Link>
        <LoginGoogle setAuth={setAuth} />
      </div>
    </div>
  );
};

export default Login;
