import React, { useState, useEffect } from "react";
import { Cart, refreshCart, fetchCartItems } from "../Cart/cart.js";
import "./item.css";

const ItemList = ({ setCartItems }) => {
  const [items, setItems] = useState([]);
  const [forceRender, setForceRender] = useState(false); // Dummy state for re-render

  useEffect(() => {
    fetch("http://localhost:3000/api/v1/products")
      .then((response) => response.json())
      .then((data) => {
        setItems(data);
      })
      .catch((error) => console.error("Error fetching items:", error));
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log("Fetching data...");
        const updatedItems = await refreshCart({ setCartItems });
        console.log("Updated items:", updatedItems);

        // Force a re-render of the component
        setForceRender((prev) => !prev);
      } catch (error) {
        console.error("Error fetching cart:", error);
      }
    };

    fetchData();
  }, [setCartItems, forceRender]);

  const handleAddToCart = async (productId) => {
    console.log("Adding product to cart:", productId);
    try {
      const userResponse = await fetch(
        "http://localhost:3000/api/v1/auth/user-data",
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            token: localStorage.token,
          },
        }
      );

      if (!userResponse.ok) {
        console.error("Failed to retrieve user information.");
        return;
      }

      const userData = await userResponse.json();
      const userId = userData.id;

      const checkCartResponse = await fetch(
        `http://localhost:3000/api/v1/cart/user/${userId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.token}`,
          },
        }
      );

      if (checkCartResponse.ok) {
        const existingCartData = await checkCartResponse.json();
        const existingCartId = existingCartData[0]?.cart_id;

        const addToCartResponse = await fetch(
          `http://localhost:3000/api/v1/cartitem`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${localStorage.token}`,
            },
            body: JSON.stringify({
              cart_id: existingCartId,
              productid: productId,
              user_id: userId,
            }),
          }
        );

        if (addToCartResponse.ok) {
          console.log("Product added to cart successfully!");

          // Refresh the cart in real-time
          await refreshCart({ setCartItems });
          const updatedItems = await fetchCartItems(setCartItems);
          setCartItems(updatedItems);
        } else {
          console.error("Failed to add product to cart.");
        }
      } else {
        // ... (same as your existing code)
      }
    } catch (error) {
      console.error("Error adding product to cart:", error);
    }
  };

  return (
    <div className="item-list-container">
      <h2 className="products-header">Products</h2>
      <ul>
        {items.map((item) => (
          <li key={item.productid} className="item">
            <div>
              <h3 className="item-name">
                {item.name} | {item.price}
              </h3>
              <p className="item-description">{item.description}</p>
              <button onClick={() => handleAddToCart(item.productid)}>
                Add to Cart
              </button>
            </div>
          </li>
        ))}
      </ul>
      <Cart setCartItems={setCartItems} />
    </div>
  );
};

export default ItemList;
