// dashboard.js
import React, { useState, useEffect } from "react";
import Header from "../Header/Header";
import ItemList from "../ProductList/Item";
import { Cart } from "../Cart/cart";
import { googleLogout } from "@react-oauth/google";
import "./Dashboard.css";

const Dashboard = ({ setAuth }) => {
  const [name, setName] = useState("");

  async function getName() {
    try {
      const response = await fetch("http://localhost:3000/api/v1/dashboard", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();

      setName(parseRes.firstname);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getName();
  }, []);

  const logout = async (e) => {
    try {
      // Sign out from Google
      await googleLogout();

      // Clear local session
      localStorage.removeItem("token");

      // Optionally force reload of the page or component
      window.location.reload("/login");

      // Or if using React Router, you could redirect to the login page
      // history.push('/login');
    } catch (error) {
      console.error("Logout failed", error);
    }
  };

  return (
    <div className="dashboard-container">
      <Header name={name} logout={logout} />
      <ItemList />
      {/* Add other components as needed */}
    </div>
  );
};

export default Dashboard;
