import React from "react";
import { useHistory } from "react-router-dom";
import logoutImage from "./pics/logout.png";
import checkoutImage from "./pics/checklist.png";
import { googleLogout } from "@react-oauth/google";
import "./Header.css";

const Header = ({ name }) => {
  const history = useHistory();

  const googleLogoutFunc = async () => {
    try {
      // If using Google's API to log out, call the appropriate method here.
      // For now, assuming `googleLogout` is a function provided by the library that handles Google logout.
      googleLogout();

      // Perform additional cleanup as needed after Google logout
      localStorage.removeItem("token"); // Remove the token from local storage
      history.push("/login"); // Redirect to login page or wherever appropriate
      console.log("Logged out from the app.");
    } catch (error) {
      console.error("Logout failed", error);
    }
  };

  const handleCheckout = () => {
    history.push("/order");
  };

  return (
    <header className="header-container">
      <button className="logout-button" onClick={googleLogoutFunc}>
        <img src={logoutImage} alt="Logout" className="logout-image" />
      </button>
      <h3 className="welcome-message">Welcome, {name}</h3>
      <h1 className="header-title">X-Fitness</h1>
      <button className="checkout-button" onClick={handleCheckout}>
        <img src={checkoutImage} alt="Checkout" className="checkout-image" />
      </button>
    </header>
  );
};

export default Header;
