// dashboard.js
import React, { useState, useEffect } from "react";
import Header from "../Header/Header";
import ItemList from "../ProductList/Item";
import { Cart } from "../Cart/cart";
import "./Dashboard.css";

const Dashboard = ({ setAuth }) => {
  const [name, setName] = useState("");
  const [cartItems, setCartItems] = useState([]);

  async function getName() {
    try {
      const response = await fetch("http://localhost:3000/api/v1/dashboard", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const parseRes = await response.json();

      setName(parseRes.firstname);
    } catch (err) {
      console.error(err.message);
    }
  }
  const handleCartUpdate = (updatedCartItems) => {
    // Do something with the updated cart items, if needed
    console.log("Cart items updated in Dashboard:", updatedCartItems);

    // You can set the updated cart items to the state if needed
    setCartItems(updatedCartItems);
  };
  useEffect(() => {
    getName();
  }, []);

  const logout = async (e) => {
    e.preventDefault();
    localStorage.removeItem("token");
    setAuth(false);
  };

  return (
    <div className="dashboard-container">
      <Header name={name} logout={logout} />
      <ItemList setCartItems={setCartItems} />
      <Cart setCartItems={setCartItems} onCartUpdate={handleCartUpdate} />
      {/* Add other components as needed */}
    </div>
  );
};

export default Dashboard;
