import React, { useState, useEffect } from "react";
import "./item.css";

const ItemList = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/api/v1/products")
      .then((response) => response.json())
      .then((data) => {
        setItems(data);
      })
      .catch((error) => console.error("Error fetching items:", error));
  }, []);

  const handleAddToCart = async (productId) => {
    console.log("Adding product to cart:", productId);
    try {
      // Step 1: Check if a cart exists for the current user
      const checkCartResponse = await fetch(
        `http://localhost:3000/api/v1/cart/user/${userId}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.token}`,
          },
        }
      );

      if (checkCartResponse.ok) {
        const existingCartData = await checkCartResponse.json();
        console.log(existingCartData);
        // If a cart already exists, use its ID to add the product to the cart
        const existingCartId = existingCartData.cart_id;

        const addToCartResponse = await fetch(
          `http://localhost:3000/api/v1/cartitem`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${localStorage.token}`,
            },
            body: JSON.stringify({
              cart_id: existingCartId,
              productid: productId,
              user_id: existingCartData.user_id, // Assuming user_id is available in existingCartData
            }),
          }
        );

        if (addToCartResponse.ok) {
          console.log("Product added to cart successfully!");
        } else {
          console.error("Failed to add product to cart.");
        }
      } else {
        // Step 2: If no cart exists, create a new cart
        const createCartResponse = await fetch(
          "http://localhost:3000/api/v1/cart",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${localStorage.token}`,
            },
          }
        );

        if (createCartResponse.ok) {
          const newCartData = await createCartResponse.json();
          const newCartId = newCartData.cart_id;

          // Use the new cart ID to add the product to the cart
          const addToNewCartResponse = await fetch(
            `http://localhost:3000/api/v1/cartitem`,
            {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${localStorage.token}`,
              },
              body: JSON.stringify({
                cart_id: newCartId,
                productid: productId,
                user_id: newCartData.user_id, // Assuming user_id is available in newCartData
              }),
            }
          );

          if (addToNewCartResponse.ok) {
            console.log("Product added to cart successfully!");
          } else {
            console.error("Failed to add product to cart.");
          }
        } else {
          console.error("Failed to create or retrieve cart.");
        }
      }
    } catch (error) {
      console.error("Error adding product to cart:", error);
    }
  };

  return (
    <div className="item-list-container">
      <h2 className="products-header">Products</h2>
      <ul>
        {items.map((item) => (
          <li key={item.productid} className="item">
            <div>
              <h3 className="item-name">
                {item.name} | {item.price}
              </h3>
              <p className="item-description">{item.description}</p>
              <button onClick={() => handleAddToCart(item.productid)}>
                Add to Cart
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ItemList;
