import React from "react";
import { useHistory } from "react-router-dom";
import { GoogleLogout } from "react-google-login"; // Import GoogleLogout
import logoutImage from "./pics/logout.png";
import checkoutImage from "./pics/checklist.png";
import "./Header.css";

// Accept onLocalLogout as a prop
const Header = ({ name, onLocalLogout }) => {
  const history = useHistory();
  const clientId =
    "65793818011-ggo7gienivg0jn9j0kfno8t37c7infam.apps.googleusercontent.com";

  const handleCheckout = () => {
    history.push("/order");
  };

  const handleLogoutSuccess = () => {
    console.log("Logged out successfully");
    onLocalLogout(); // Call the local logout function passed via props
    history.push("/login"); // Redirect to login page
  };

  return (
    <header className="header-container">
      {/* Replace the original logout button with GoogleLogout */}
      <GoogleLogout
        clientId={clientId}
        buttonText="Logout"
        onLogoutSuccess={handleLogoutSuccess}
        render={(renderProps) => (
          <button
            onClick={renderProps.onClick}
            disabled={renderProps.disabled}
            className="logout-button"
          >
            <img src={logoutImage} alt="Logout" className="logout-image" />
          </button>
        )}
      />
      <h3 className="welcome-message">Welcome, {name}</h3>
      <h1 className="header-title">X-Fitness</h1>
      <button className="checkout-button" onClick={handleCheckout}>
        <img src={checkoutImage} alt="Checkout" className="checkout-image" />
      </button>
    </header>
  );
};

export default Header;
