import React, { Component } from "react";
import { GoogleLogin } from "react-google-login";
import "./login.css"; // Import your login styles

const clientId =
  "65793818011-ggo7gienivg0jn9j0kfno8t37c7infam.apps.googleusercontent.com";

class LoginGoogle extends Component {
  onSuccess = (res) => {
    console.log("LOGIN SUCCESS! Current User: ", res.profileObj);
  };

  onFailure = (res) => {
    console.log("LOGIN FAILED! res: ", res);
  };

  render() {
    return (
      <div id="signInButton">
        <GoogleLogin
          clientId={clientId}
          buttonText="Login"
          onSuccess={this.onSuccess}
          onFailure={this.onFailure}
          cookiePolicy={"single_host_origin"}
          isSignedIn={true}
        />
      </div>
    );
  }
}

class Login extends Component {
  state = {
    username: "",
    password: "",
  };

  handleLogin = (e) => {
    e.preventDefault();
    // Your existing login logic here (optional if you want to handle traditional login)
  };

  render() {
    const { username, password } = this.state;

    return (
      <div className="login-container">
        <h2>Login</h2>
        <form onSubmit={this.handleLogin}>
          <label>
            Username:
            <input
              type="text"
              value={username}
              onChange={(e) => this.setState({ username: e.target.value })}
            />
          </label>
          <br />
          <label>
            Password:
            <input
              type="password"
              value={password}
              onChange={(e) => this.setState({ password: e.target.value })}
            />
          </label>
          <br />
          <button type="submit">Login</button>
        </form>

        {/* Render the Google login component */}
        <LoginGoogle />
      </div>
    );
  }
}

export { Login, LoginGoogle };
