import React, { useState } from "react";
import { GoogleLogin } from "@react-oauth/google";
import "./login.css";
import { decodeJwtToken } from "../Util/SessionStart"; // Import the decoding function

const clientId =
  "65793818011-ggo7gienivg0jn9j0kfno8t37c7infam.apps.googleusercontent.com";

export const LoginGoogle = () => {
  const onSuccess = (res) => {
    console.log("LOGIN SUCCESS! Response: ", res);

    const userProfile = res?.profileObj || res?.profileObj;

    if (userProfile) {
      console.log("User Profile: ", userProfile);

      // Call the decoding function and pass the token
      decodeJwtToken(userProfile.tokenId);
    } else {
      console.log("User Profile not found in the response.");
    }
  };

  const onFailure = (res) => {
    console.log("LOGIN FAILED! Response: ", res);
  };

  return (
    <div id="signInButton">
      <GoogleLogin
        clientId={clientId}
        buttonText="Login"
        onSuccess={onSuccess}
        onFailure={onFailure}
        cookiePolicy={"single_host_origin"}
        isSignedIn={true}
      />
    </div>
  );
};

export const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    // Your existing login logic here (optional if you want to handle traditional login)
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>

      {/* Render the Google login component */}
      <LoginGoogle />
    </div>
  );
};
